def get_all_news_links(search, start_date, end_date):
    """í˜ì´ì§€ë„¤ì´ì…˜ì„ ìë™ íƒìƒ‰í•˜ì—¬ ëª¨ë“  ë‰´ìŠ¤ ê¸°ì‚¬ ë§í¬ë¥¼ ê°€ì ¸ì˜´"""
    base_url = "https://www.ytn.co.kr/search/index.php"
    page = 1
    news_links = set()  # ì¤‘ë³µ ì œê±°ë¥¼ ìœ„í•´ set ì‚¬ìš©

    while True:
        params = {
            "type": 1, "callSite": 1,
            "q": search,
            "ds": start_date, "de": end_date,
            "se_date": 3, "target": 0, "mtarget": 0,
            "page": page  # í˜ì´ì§€ ë²ˆí˜¸ ì¶”ê°€
        }
        headers = {"User-Agent": "Mozilla/5.0"}
        response = requests.get(base_url, params=params, headers=headers)
        soup = BeautifulSoup(response.text, "html.parser")

        # ë‰´ìŠ¤ ê¸°ì‚¬ ë§í¬ ê°€ì ¸ì˜¤ê¸°
        articles = soup.select("div.search_news_list a[href]")
        links = {urljoin("https://www.ytn.co.kr", a["href"]) for a in articles}

        if not links:
            print(f"í˜ì´ì§€ {page}ì— ë” ì´ìƒ ê¸°ì‚¬ê°€ ì—†ìŠµë‹ˆë‹¤. í¬ë¡¤ë§ ì¢…ë£Œ.")
            break  # ë” ì´ìƒ ê¸°ì‚¬ê°€ ì—†ìœ¼ë©´ ì¢…ë£Œ

        news_links.update(links)  # ì¤‘ë³µ ì œê±°í•˜ë©´ì„œ ë§í¬ ì¶”ê°€
        print(f"í˜ì´ì§€ {page} í¬ë¡¤ë§ ì™„ë£Œ. {len(links)}ê°œ ê¸°ì‚¬ ì¶”ê°€ë¨.")
        page += 1  # ë‹¤ìŒ í˜ì´ì§€ë¡œ ì´ë™

        time.sleep(1)  # ê³¼ë¶€í•˜ ë°©ì§€ë¥¼ ìœ„í•œ ë”œë ˆì´ ì¶”ê°€

    return list(news_links)

def fetch_news_content(url):
    """ê¸°ì‚¬ì˜ ì œëª©, ë‚ ì§œ, ë³¸ë¬¸ì„ ê°€ì ¸ì˜´"""
    headers = {"User-Agent": "Mozilla/5.0"}
    response = requests.get(url, headers=headers)
    soup = BeautifulSoup(response.text, "html.parser")

    # ê¸°ì¡´ CSS ì„ íƒì ì‚¬ìš©
    title_element = soup.select_one("h2.news_title span")
    title = title_element.get_text(strip=True) if title_element else "(ì œëª© ì—†ìŒ)"

    date_element = soup.select_one("div.date")
    date = date_element.get_text(strip=True) if date_element else "(ë‚ ì§œ ì—†ìŒ)"

    content_elements = soup.select("div#CmAdContent.paragraph span")
    content = " ".join([p.get_text(strip=True) for p in content_elements]) if content_elements else "(ë‚´ìš© ì—†ìŒ)"

    print(f"[DEBUG] URL: {url}")
    print(f"[DEBUG] Title: {title}")
    print(f"[DEBUG] Date: {date}")
    print(f"[DEBUG] Content Length: {len(content)}")

    return date, title, url, content

def main():
    """ë©”ì¸ í•¨ìˆ˜"""
    search = input("ê²€ìƒ‰ì–´ë¥¼ ì…ë ¥í•˜ì„¸ìš”: ")
    start_date = input("ê²€ìƒ‰ ì‹œì‘ ë‚ ì§œ (YYYYMMDD): ")
    end_date = input("ê²€ìƒ‰ ì¢…ë£Œ ë‚ ì§œ (YYYYMMDD): ")
    save_path = setup_colab()

    # ì „ì²´ ê¸°ì‚¬ ë§í¬ ìˆ˜ì§‘
    news_links = get_all_news_links(search, start_date, end_date)
    print(f"ì´ {len(news_links)}ê°œì˜ ê¸°ì‚¬ ë§í¬ë¥¼ ì°¾ì•˜ìŠµë‹ˆë‹¤.")

    # ê¸°ì‚¬ ë‚´ìš© í¬ë¡¤ë§
    news_data = [fetch_news_content(url) for url in news_links]

    # ë°ì´í„° ì €ì¥
    df = pd.DataFrame(news_data, columns=['date', 'title', 'link', 'content'])
    df.drop_duplicates(subset=['title', 'content'], inplace=True)
    file_name = f"{search}_{start_date}_{end_date}.csv"
