def is_invalid_location(title):
    location_keywords = ["ì‹œ", "ë„", "êµ¬", "êµ°", "ì", "ë©´", "ë™", "ë¦¬"]
    keep_keywords = ["í•™êµ", "ëŒ€í•™", "ì—°êµ¬ì†Œ", "ì„¼í„°", "ê¸°ê´€", "í˜‘íšŒ", "ë³‘ì›", "ë°•ë¬¼ê´€"]

    # í•™êµ, ì—°êµ¬ì†Œ ë“±ì˜ í‚¤ì›Œë“œê°€ í¬í•¨ëœ ê²½ìš° ìœ ì§€
    if any(kw in title for kw in keep_keywords):
        return False

    # ë‘ ê°œ ì´ìƒì˜ ì§€ì—­ëª…ë§Œ í¬í•¨ëœ ê²½ìš° ì œê±°
    words = title.split()
    if len(words) >= 2 and all(any(loc in word for loc in location_keywords) for word in words):
        return True

    return False

