def extract_nouns(text):
    words = word_tokenize(text)  # ë‹¨ì–´ í† í°í™”
    tagged = pos_tag(words)  # í’ˆì‚¬ íƒœê¹…
    places = [word for word, pos in tagged if pos in ["NNP", "NN"]]  # ê³ ìœ ëª…ì‚¬/ëª…ì‚¬ í•„í„°ë§
    return ", ".join(places) if places else None

